import React, { useContext } from "react";
import "./Home.css";
import Vector1 from "../../Images/pngwing.com (2).png";
import Vector2 from "../../Images/purpleblur.png";
import boy from "../../Images/download (5).png";
import glassesimoji from "../../Images/glassesimoji.png";
import thumbup from "../../Images/thumbup.png";
import crown from "../../Images/crown.png";
import FloatinDiv from "../FloatingDiv/FloatingDiv";
import Github from "../../Images/github.png";
import LinkedIn from "../../Images/linkedin.png";
import Instagram from "../../Images/instagram.png";
import { motion } from "framer-motion";
import { Link } from "react-scroll";
const Home = () => {
  // Transition
  const transition = { duration: 2, type: "spring" };



  return (
    <div className="Intro" id="Intro">
      {/* left name side */}
      <div className="i-left">
        <div className="i-name">
          {/* yahan change hy darkmode ka */}
          <span >Hey! I Am</span>
          <span>Deepak Kumar</span>
          <span>
            Frontend Developer with high level of experience in web designing
            and development, producting the Quality work
          </span>
        </div>
        <Link to="contact" smooth={true} spy={true}>
          <button className="button i-button">Hire me</button>
        </Link>
        {/* social icons */}
        <div className="i-icons">
          <a href="https://github.com/Deepakkumar984"><img src={Github} alt="" /></a>
          <a href="https://www.linkedin.com/in/deepak-kumar-9ba1721b8/"> <img src={LinkedIn} alt="" /></a>
          <a href="https://www.instagram.com/deep_kanyalal/"><img src={Instagram} alt="" /></a>
        </div>
      </div>
      {/* right image side */}
      <div className="i-right">
        <img src={Vector1} alt="" />
        <img src={Vector2} alt="" />
        <img src={boy} alt="" />
        {/* animation */}
        <motion.img
          initial={{ left: "-36%" }}
          whileInView={{ left: "-24%" }}
          transition={transition}
          src={glassesimoji}
          alt=""
        />

        <motion.div
          initial={{ top: "-4%", left: "74%" }}
          whileInView={{ left: "68%" }}
          transition={transition}
          className="floating-div"
        >
          <FloatinDiv img={crown} text1="Web" text2="Developer" />
        </motion.div>

        {/* animation */}
        <motion.div
          initial={{ left: "9rem", top: "18rem" }}
          whileInView={{ left: "2rem" }}
          transition={transition}
          className="floating-div"
        >
          {/* floatinDiv mein change hy dark mode ka */}
          <FloatinDiv img={thumbup} text1="Best Design" text2="Award" />
        </motion.div>

        <div className="blur" style={{ background: "rgb(238 210 255)" }}></div>
        <div
          className="blur"
          style={{
            background: "#C1F5FF",
            top: "17rem",
            width: "21rem",
            height: "11rem",
            left: "-9rem",
          }}
        ></div>
      </div>
    </div>
  );
};

export default Home;
