import React from 'react';

import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';
import { Link } from 'react-scroll';

const content = [
    {
        title: ' Hi! Welcome to Synergy   ',
        description:
            'Integrated Solution ',

    },
    {
        title: ' We Deals   ',
        description:
            'Integrated Solution ',

    },
    {
        title: ' Hi! Welcome to Synergy   ',
        description:
            'Integrated Solution ',

    },
    {
        title: ' Hi! Welcome to Synergy   ',
        description:
            'Integrated Solution ',

    },


];


const Apple = () => {
    return (
        <div id='flex' >
            <div A-right>


                <Slider autoplay={3000}  >
                    {content.map((item, index) => (
                        <div
                            key={index}
                        >
                            <div className="center">
                                <h1>{item.title}</h1>
                                <p>{item.description}</p>
                            </div>

                        </div>
                    ))}

                </Slider>
                <Link to='contact' spy={true} smooth={true} >
                    <button className='I-button' >Contact Us</button>

                </Link>
            </div>
            <div className='A-left'></div>
        </div>
    );
}

export default Apple
