import React, { useState } from 'react';

import './Contact.css'
export const Contact1 = () => { 
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:3001/send-email', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Message sent successfully!');
        // Clear the form after successful submission
        setFormData({
          name: '',
          email: '',
          message: '',
        });
      } else {
        alert('Failed to send the message.');
      }
    } catch (error) {
      console.error('Error sending the message:', error);
      alert('Failed to send the message.');
    }
  };

  return (
    <div id='Contact'>
    <section id="section-wrapper">
      <div class="box-wrapper">
        <div class="info-wrap">

        </div>
        <div class="form-wrap">
          <form action="#" method="POST">
            <h2 class="form-title">Send us a message</h2>
            <div class="form-fields">
              <div class="form-group">
                <input type="text" onChange={handleChange} className="fname" name="name" value={formData.name} placeholder="First Name"></input>
              </div>
              <div class="form-group">
                <input type="text" onChange={handleChange} className="lname" name="name" value={formData.name} placeholder="Last Name"></input>
              </div>
              <div class="form-group">
                <input type="email" onChange={handleChange} className="email" name="name" value={formData.name} placeholder="Mail"></input>
              </div>
              <div class="form-group">
                <input type="number" onChange={handleChange} className="phone" name="name" value={formData.name} placeholder="Phone"></input>
              </div>
              <div class="form-group">
                <textarea name="message" onChange={handleChange} id=""  value={formData.name} placeholder="Write your message"></textarea>
              </div>
            </div>
            <input type="submit" value="Send Message" className="submit-button"></input>
          </form>
        </div>
      </div>
    </section>


  </div>
  );
};
