import React from 'react'
import "./Intro.css"
import { Link } from 'react-router-dom'
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';

import Vector1 from "../Images/ideas-and-inspiration-in-learning-and-education-people-sitting-on-books-modern-online-learning-lamp-bulb-and-pencil-education-business-illustration-for-business-card-banner-brochure-flyer-free-vector-.png";
const content = [
  {
      title: ' Hi! Welcome to Synergy   ',
      description:
          'Integrated Solution ',

  },
  {
      title: ' We Deals   ',
      description:
          'Integrated Solution ',

  },
  {
      title: ' Hi! Welcome to Synergy   ',
      description:
          'Integrated Solution ',

  },
  {
      title: ' Hi! Welcome to Synergy   ',
      description:
          'Integrated Solution ',

  },


];
export const Intro = () => {
  return (
    <div className="Intro" id="Intro">
      {/* left name side */}
      <div className="i-left">
      <div className='content'>
      <Slider autoplay={3000}  >
                    {content.map((item, index) => (
                        <div
                            key={index}
                        >
                            <div className="center">
                                <h1>{item.title}</h1>
                                <p>{item.description}</p>
                            </div>

                        </div>
                    ))}

                </Slider>
                
        <br></br>
        <div>
          <Link to='Contact' className='btn '>Let's discuss your project</Link>
        </div>
      </div>
      </div>
      {/* right image side */}
      <div className="i-right">
     
      <img alt='' className='I-img' src={Vector1} ></img>

      </div>
    </div>
  );
};
