import React from 'react'
import './Contact.css'
import {  FaAddressBook } from 'react-icons/fa';
import emailjs from "@emailjs/browser";

export const Contact1 = () => {
  const form = useRef();
  const [done, setDone] = useState(false)
  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_gvjhfwb",
        "template_tjy7on8",
        form.current,
        "SAaE8zJpSGN_mPoK8"
      )
      .then(
        (result) => {
          console.log(result.text);
          setDone(true);
          form.reset();
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <div id='Contact'>

    <h1 className='c-heading'>  CONTACT US <FaAddressBook style={{color:'#3da09e'}}></FaAddressBook></h1>
      <section id="section-wrapper">
        <div class="box-wrapper">
          <div class="info-wrap">

          </div>
          <div class="form-wrap">
            <form action="#" method="POST" ref={form} onSubmit={sendEmail}>
              <h2 class="form-title">Send us a message</h2>
              <div class="form-fields">
                <div class="form-group">
                  <input type="text" class="fname" placeholder="First Name"></input>
                </div>
                <div class="form-group">
                  <input type="text" class="lname" placeholder="Last Name"></input>
                </div>
                <div class="form-group">
                  <input type="email" class="email" placeholder="Mail"></input>
                </div>
                <div class="form-group">
                  <input type="number" class="phone" placeholder="Phone"></input>
                </div>
                <div class="form-group">
                  <textarea name="message" id="" placeholder="Write your message"></textarea>
                </div>
              </div>
              <input type="submit" value="Send Message" class="submit-button"></input>
            </form>
          </div>
        </div>
      </section>


    </div>
  )
}
