import React, { useState, useEffect } from 'react';
import "./Intro.css"
import { Link } from 'react-router-dom'
import Typewriter from "typewriter-effect";

import Vector1 from "../Images/concept-of-data-analysis-and-maintenance.png";

const listItems = [
  { title: 'step 1', content: 'Break The UI Into A Component Hierarchy',button: '' },
  { title: 'step 2', content: 'Build A Static Version in React' },
  { title: 'step 3', content: 'Identify The Minimal (but complete) Representation Of UI State' },
  { title: 'step 4', content: 'Identify Where Your State Should Live' },
  { title: 'step 5', content: 'Add Inverse Data Flow' },
];

const SlideItem = ({ title, content }) => (
  <div className="item-slide">
    <h1>{title}</h1>
    <p>{content}</p>
    {title === 'step 1' && (
      <div>
      <Link to='Contact' className='btn '>Let's discuss your project</Link>
    </div>      )}
  </div>
);

export const Intro = () => {
  const [slideIndex, setSlideIndex] = useState(0);

  useEffect(() => {
    const timeout = setTimeout(goToNextSlide, 2000);

    return () => clearTimeout(timeout);
  }, [slideIndex]);

 

  const goToNextSlide = () => {
    setSlideIndex((prevIndex) => (prevIndex === listItems.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className='intro' id='Home'>

      <div className='content'>
      <div className="slideshow-simple">
      <SlideItem {...listItems[slideIndex]} />
    
    </div>
        <br></br>
      
      </div>
      <img alt='' className='I-img' src={Vector1} ></img>
    </div>


  )
}
