import React from 'react'
import "./Intro.css"
import { Link } from 'react-router-dom'
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';

import Vector1 from "../Images/ideas-and-inspiration-in-learning-and-education-people-sitting-on-books-modern-online-learning-lamp-bulb-and-pencil-education-business-illustration-for-business-card-banner-brochure-flyer-free-vector-.png";
const content = [
  {
      title: ' Hi! Welcome to Synergy   ',
      description:
          'Integrated Solution ',

  },
  {
      title: ' We Deals   ',
      description:
          'Integrated Solution ',

  },
  {
      title: ' Hi! Welcome to Synergy   ',
      description:
          'Integrated Solution ',

  },
  {
      title: ' Hi! Welcome to Synergy   ',
      description:
          'Integrated Solution ',

  },


];
export const Intro = () => {
  return (
    <div className='intro' id='Home'>

      <div className='content'>
        <Typewriter

          onInit={(typewriter) => {
            typewriter
              .typeString("<p> Hi! Welcome to Synergy </p>         <h1> Integrated Solution</h1>")
              .pause()

              .start()




          }}

          options={{
            strings: ['<p> Hi! Welcome to Synergy </p> <h1> Integrated Solution</h1>'],
            autoStart: true,
            cursor: '',
            deleteSpeed: 1,
            // pauseFor:200000,
          }}

        />

        <br></br>
        <div>
          <Link to='Contact' className='btn '>Let's discuss your project</Link>
        </div>
      </div>
      <img alt='' className='I-img' src={Vector1} ></img>
    </div>


  )
}
