import React from 'react';

import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';
import 'normalize.css/normalize.css';
import './Componets/slider-animations.css';
import './Componets/I.css';
import { Link } from 'react-scroll';

const content = [
    {
      title: ' Hi! Welcome to Synergy   ',
      description:
        'Integrated Solution ',
      button: 'Contact for',
  
    },
    {
        title: ' Hi! Welcome to Synergy   ',
        description:
          'Integrated Solution ',
        button: 'Contact love',
    
      },
      {
        title: ' Hi! Welcome to Synergy   ',
        description:
          'Integrated Solution ',
        button: 'Contact Us',
    
      },
      {
        title: ' Hi! Welcome to Synergy   ',
        description:
          'Integrated Solution ',
        button: 'Contact give',
    
      },
  
  
  ];
  

const Apple = () => {
    return (
        <div  id='OurServices' >
    
    
    <Slider autoplay={3000}>
	{content.map((item, index) => (
		<div
			key={index}
			style={{ background: `url('${item.image}') no-repeat center center` }}
		>
			<div className="center">
				<h1>{item.title}</h1>
				<p>{item.description}</p>
				<button>{item.button}</button>
			</div>
		</div>
	))}
</Slider>
        </div>
      );
    }
    
export default Apple
