import React, { useState } from 'react';
import axios from 'axios';

import './Contact.css'
import { FaAddressBook } from 'react-icons/fa';
import { useForm, ValidationError } from '@formspree/react';
export const Contact1 = () => { 
  const [emailData, setEmailData] = useState({
    name:'',
    lastname:'',
    to: '',
    number: '',
    message: '',
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmailData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post('/send-email', emailData)
      .then((response) => {
        console.log(response.data);
        // Handle success, show a success message, or perform any other action
      })
      .catch((error) => {
        console.error('Error:', error);
        // Handle error, show an error message, or perform any other action
      });
  };

  return (
    <div id='Contact'>

      <h1 className='c-heading'>  CONTACT US <FaAddressBook style={{ color: '#3da09e' }}></FaAddressBook></h1>
      <section id="section-wrapper">
        <div class="box-wrapper">
          <div class="info-wrap">

          </div>
          <div class="form-wrap">
            <form 
              onSubmit={handleSubmit}>
              <h2 class="form-title">Send us a message</h2>
              <div class="form-fields">
