                </div>

                <div class="form-group">
                  <input type="text" name="lastname" class="lname" placeholder="Last Name"></input>
                  <ValidationError
                    prefix="Text"
                    field="text"
                    errors={state.errors}
                  />
                </div>
                <div class="form-group">
                  <input type="email" name="to" class="email" placeholder="Mail"></input>
                  <ValidationError
                    prefix="Email"
                    field="email"
                    errors={state.errors}
                  />
                </div>
                <div class="form-group">
                  <input type="number" name="number" class="phone" placeholder="Phone"></input>
                  <ValidationError
                    prefix="Number"
                    field="number"
                    errors={state.errors}
                  />
                </div>
                <div class="form-group">
                  <textarea name="message" id="" placeholder="Write your message"></textarea>
                  <ValidationError
