import React, { useState, useEffect } from 'react';
import "./Intro.css"
import { Link } from 'react-router-dom'

import Vector1 from "../Images/ideas-and-inspiration-in-learning-and-education-people-sitting-on-books-modern-online-learning-lamp-bulb-and-pencil-education-business-illustration-for-business-card-banner-brochure-flyer-free-vector-.png";

const listItems = [
  { title: 'Hi! Welcome to ', content: 'Synergy Integrated Solution' },
  { title: 'We Deals', content: 'Web Designing and Development' },
  { title: 'We Deals', content: 'Software Development' },
  { title: 'We Deals', content: 'School Management System' },
  { title: 'We Deals', content: 'CRM (Customer Relationship Management)' },
  { title: 'We Deals', content: 'ERP (Enterprises Resoures Planning)' },
  { title: 'We Deals', content: 'Mobile Apps' },
  { title: 'We Deals', content: 'Digital Marketing' },
  { title: 'We Deals', content: 'Branding and Creative Designing' },
];

const SlideItem = ({ title, content }) => (
  <div className="item-slide">
    <h1>{title}</h1>
    <p>{content}</p>
    {title === 'Hi! Welcome to ' && (
      <div>
      <Link to='Contact' className='btn I-button '>Let's discuss your project</Link>
    </div>      )}
  </div>
);

export const Intro = () => {
  const [slideIndex, setSlideIndex] = useState(0);

  useEffect(() => {
    const timeout = setTimeout(goToNextSlide, 2000);

    return () => clearTimeout(timeout);
  }, [slideIndex]);

 

  const goToNextSlide = () => {
    setSlideIndex((prevIndex) => (prevIndex === listItems.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div className='intro' id='Home'>

      <div className='content'>
      <div className="slideshow-simple">
      <SlideItem {...listItems[slideIndex]} />
    
    </div>
        <br></br>
      
      </div>
      <img alt='' className='I-img' src={Vector1} ></img>
    </div>


  )
}
