import React, { useState } from 'react';
import axios from 'axios';

import './Contact.css'
import { FaAddressBook } from 'react-icons/fa';
export const Contact1 = () => { 
  const [emailData, setEmailData] = useState({
    name:'',
    lastname:'',
    to: '',
    phone: '',
    message: '',
  })

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmailData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post('/send-email', emailData)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error('Error:', error);
        // Handle error, show an error message, or perform any other action
      });
  };

  return (
    <div id='Contact'>

      <h1 className='c-heading'>  CONTACT US <FaAddressBook style={{ color: '#3da09e' }}></FaAddressBook></h1>
      <section id="section-wrapper">
        <div class="box-wrapper">
          <div class="info-wrap">

          </div>
          <div class="form-wrap">
            <form 
              onSubmit={handleSubmit}>
              <h2 class="form-title">Send us a message</h2>
              <div class="form-fields">
                <div class="form-group">
                  <input onChange={handleChange} type="text" value={emailData.name} name="name" class="fname" placeholder="First Name"></input>

                </div>

                <div class="form-group">
                  <input onChange={handleChange} type="text" value={emailData.lastname} name="lastname" class="lname" placeholder="Last Name"></input>

                </div>
                <div class="form-group">
                  <input onChange={handleChange} type="email" value={emailData.to} name="to" class="email" placeholder="Mail"></input>

                </div>
                <div class="form-group">
                  <input onChange={handleChange} type="number" name="phone" value={emailData.phone} class="phone" placeholder="Phone"></input>

                </div>
                <div class="form-group">
                  <textarea onChange={handleChange} name="message" value={emailData.message} id="" placeholder="Write your message"></textarea>

                </div>
              </div>
              <input  type="submit" value="Send Message" class="submit-button"></input>

            </form>
          </div>
        </div>
      </section>


    </div>
  )
}
