import React from 'react';
import Vector1 from "./Componets/Images/human-resource-management-removebg-preview.png";

import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';
import { Link } from 'react-scroll';

const content = [
    {
      title: ' Hi! Welcome to Synergy   ',
      description:
        'Integrated Solution ',
  
    },
    {
        title: ' We Deals   ',
        description:
          'Integrated Solution ',
    
      },
      {
        title: ' Hi! Welcome to Synergy   ',
        description:
          'Integrated Solution ',
    
      },
      {
        title: ' Hi! Welcome to Synergy   ',
        description:
          'Integrated Solution ',
    
      },
  
  
  ];
  

const Apple = () => {
    return (
        <div  id='flex' >
    <div A-right>

    
         <Slider autoplay={3000}  >
                {content.map((item, index) => (
              <div
                key={index}
              >
                <div className="center">
                  <h1>{item.title}</h1>
                  <p>{item.description}</p>
                </div>
    
              </div>
            ))}
    
          </Slider>
                  <Link to='contact' spy={true} smooth={true} >
                  <button className='I-button' >Contact Us</button>
    
                  </Link>
        </div>
        <div className='i-right'>
        <img alt='' className='I-img' src={Vector1} ></img>

        </div>
    </div>
      );
    }
    
export default Apple
